        console.log(e.target.value)
      }

    render(){
        return (
            <div>
                The length of the series array is : {this.state.series.length}
                <div>
                  <input type="text" onChange={this.onSeriesInputChange} />
                </div>
                <SeriesList list = {this.state.series}/>
            </div>
        )
    }
}

export default Series;
